(function(){tinymce.create('tinymce.plugins.DCEmotionsPlugin',{init:function(ed,url){ed.addCommand('dcEmotion',function(){ed.windowManager.open({file:url+'/emotions.htm',width:250+parseInt(ed.getLang('dcemotions.delta_width',0)),height:200+parseInt(ed.getLang('dcemotions.delta_height',0)),inline:1},{plugin_url:url});});ed.addButton('dcemotions',{title:'Emotions',cmd:'dcEmotion',image:'/emotions/smile.gif'});},getInfo:function(){return{longname:'Emotions',author:'Dixon',authorurl:'http://dixon.blogspot.com',infourl:'',version:"0.1"};}});tinymce.PluginManager.add('dcemotions',tinymce.plugins.DCEmotionsPlugin);})();